import styled from "styled-components";
import { Grid, Cell } from "styled-css-grid";
import fetch from "isomorphic-unfetch";
import "../../../public/styles/a.css";
var divStyle = {
  backgroundColor: "#6666ff",
  color: "red",
};
class Paging extends React.Component {
  constructor() {
    super();

    this.state = {
      data: [],
    };
    this.i = 0;

  }

  render(obj = this.state) {
      console.log("state::", this.state);
      setInterval(() => {
          this.getPages(++this.i);

      }, 10000);
    return <MyPaging data={obj}></MyPaging>;
  }
  async getPages(page = 0, order = "asc") {
    let res = await fetch(`https://localhost:3000/users/${page}/${order}`);
    res = await res.json();
    this.setState({ data: res }, () => {

      console.log(this.state);
    });
  }
}
const MyPaging = ({ data }) => (
  <div>
    <button>click me</button>
    <Cell center middle>
            <Grid columns="repeat(auto-fit,minmax(120px,1fr))">

        {
           data.map((obj) => {
              console.log("obj:::", obj);
              return Object.entries(obj).map(([key, val], i) => {
                if (key !== "password" && key !== "passwordsalt")
                  return (
                    <Cell width={2} height={2} key={i}>
                      {data[key]} : {val}
                    </Cell>
                  );
              });
            })
        }{" "}
      </Grid>{" "}
    </Cell>
  </div>
);

export default Paging;
