import fetch from 'isomorphic-unfetch';
import Link from 'next/link';
import React from "react";

import "../public/styles/a.css";

// const da =postgres.getUsers();

const Index = (props) => (

<div className="wrapper">
  <div className="box a">A</div>
  <div className="box b">B</div>
  <div className="box c">C</div>
  <div className="box d">
    <div className="box e">E</div>
    <div className="box f">F</div>
    <div className="box g">G</div>
        </div>
        <div >
            {props.data.map(( obj, i) =>
            {
               return  Object.entries(obj).map( ([key, val]) => {


                return < div className="whatEver" key={i} > {key}:{val}  </div>

                });


            })}
       </div >


       < li >
           < Link href="/about" >
               <a> about </a>
           </Link >
           </li> 
</div>

       )
        Index.getInitialProps = async() => {
            const pageRequest = `https://localhost:3000/users`;
            const res = await fetch(pageRequest)
            const json = await res.json();

            return {
                data: json
            }
        }

export default Index;
