import styled from "styled-components";
import { Grid, Cell } from "styled-css-grid";
import fetch from "isomorphic-unfetch";
import "../../../public/styles/a.css";
var divStyle = {
  backgroundColor: "#6666ff",
  color: "red",
};
class Paging extends React.Component {
  constructor() {
    super();

    this.state = {
      data: [],
    };
    this.i = 0;
  }

  render() {
    console.log("state::", this.state);
    setInterval(() => {
      this.getPages(++this.i);
    }, 15000);
    return <MyPaging data={this.state}></MyPaging>;
  }
  async getPages(page = 0, order = "asc") {
    let res = await fetch(`https://localhost:3000/users/${page}/${order}`);
    res = await res.json();
    this.state.data = res.map(objects => Object.entries(objects))
    this.setState({ data: res }, () => {
      console.log("state---->", this.state);
    });
  }
}
const MyPaging = ({ data }) => (
  <div>
    <button>click me</button>
    <Cell center middle>
      <Grid columns="repeat(auto-fit,minmax(120px,1fr))">
        {JSON.stringify(data)}{" "}
      </Grid>{" "}
    </Cell>
  </div>
);

export default Paging;
