import fetch from "isomorphic-unfetch";
import Link from "next/link";
import  React from "react";
import Modal from "../portal/Modal";
import "../public/styles/a.css";
import Login from "./login/login";
import styled from "styled-components";
import { Grid, Cell } from "styled-css-grid";


const MyGrid = ({data}) => (
    <Grid columns={data.length} gap="2px"> 
    <Modal />

    {data.map(obj => {
      return Object.entries(obj).map(([key, val], i) => {
        return (
          <Cell className="whatEver" key={i}>
          {/* {key} : {val} */}
          </Cell>
        );
      });
    })}
  </Grid>
);

const Index = ({ data,log_in }) =><div> {JSON.stringify(log_in)} <MyGrid data={data}></MyGrid></div>;
Index.getInitialProps = async () => {
  const pageRequest = `https://localhost:3000/users`;
  const login = `https://localhost:3000/login?name=George&password=password2`;
  const res = await fetch(pageRequest);
  const loginres = await fetch(login);

  const log_in = await loginres.json();
  const data = await res.json()
 console.log("-------> logs --->",log_in)
  return {
    log_in,
    data
  };
};
export default Index;
