import fetch from 'isomorphic-unfetch';
import Link from 'next/link';
import React from "react";

import "../public/styles/a.css";

// const da =postgres.getUsers();

const Index = (props) => (

<div class="grid">
<header>
  Header
</header>

<aside className="sidebar-left">
  Left Sidebar
</aside>

<article>
  Article
</article>

<aside className="sidebar-right">
  Right Sidebar
</aside>

<footer>
  Footer
  <ul>

       <div >
                    {props.data.map((home, i) => < div className="whatEver" key={i} > {JSON.stringify(home)} </div>)}
       </div >


       < li >
           < Link href="/about" >
               <a> about </a>
           </Link >
           </li> 

        </ul>
</footer>
</div>
       )
        Index.getInitialProps = async() => {
            const pageRequest = `https://localhost:3000/users`;
            const res = await fetch(pageRequest)
            const json = await res.json();

            return {
                data: json
            }
        }

export default Index;
