import fetch from 'isomorphic-unfetch';
import Link from 'next/link';
import React from "react";

import styled from 'styled-components';
import { Grid, Cell } from "styled-css-grid";
const MyGrid = (val) => (

//    <Grid>{ val.object.length }  </Grid>   
    <Grid columns={val.object.length} gap="2px">
        {
            val.object.map((obj) =>
            {
               return  Object.entries(obj).map( ([key, val], i) => {
                    return <Cell className="whatEver" key={i} > {key} : {val}  </Cell>
               });

        // <Cell>{JSON.stringify(val)}</Cell>
            }

            )
        }
         </Grid>
);

const Button = styled.button`
  color: palevioletred;
  font-size: 1em;
  margin: 1em;
  padding: 0.25em 1em;
  border: 2px solid palevioletred;
  border-radius: 3px;
`;

// A new component based on Button, but with some override styles
const TomatoButton = styled(Button)`
  color: tomato;
  border-color: tomato;
`;

import Login from "./login/login";

import "../public/styles/a.css";

